import React, { useState } from "react";
import ReactDOM from "react-dom/client";
import "./index.css";
import { Header } from "./Header";
import { Menu } from "./Menu";
import { Footer } from "./Footer";

export const pizzaData = [
  {
    name: "Focaccia",
    ingredients: "Bread with italian olive oil and rosemary",
    price: 6,
    photoName: "pizzas/focaccia.jpg",
    soldOut: false,
  },
  {
    name: "Pizza Margherita",
    ingredients: "Tomato and mozarella",
    price: 10,
    photoName: "pizzas/margherita.jpg",
    soldOut: true,
  },
  {
    name: "Pizza Spinaci",
    ingredients: "Tomato, mozarella, spinach, and ricotta cheese",
    price: 12,
    photoName: "pizzas/spinaci.jpg",
    soldOut: false,
  },
  {
    name: "Pizza Funghi",
    ingredients: "Tomato, mozarella, mushrooms, and onion",
    price: 12,
    photoName: "pizzas/funghi.jpg",
    soldOut: false,
  },
  {
    name: "Pizza Salamino",
    ingredients: "Tomato, mozarella, and pepperoni",
    price: 15,
    photoName: "pizzas/salamino.jpg",
    soldOut: true,
  },
  {
    name: "Pizza Prosciutto",
    ingredients: "Tomato, mozarella, ham, aragula, and burrata cheese",
    price: 18,
    photoName: "pizzas/prosciutto.jpg",
    soldOut: false,
  },
];

function App() {
  const [cartItems, setCartItems] = useState([{ name: [], quantity: 0 }]);
  const handleDelete = (pizzaName) => {
    // Find the pizza in cartItems by its name
    const existingPizza = cartItems.find((pizza) => pizza.name === pizzaName);

    if (existingPizza && existingPizza.quantity > 0) {
      // Decrease the quantity by 1
      const updatedQuantity = existingPizza.quantity - 1;

      if (updatedQuantity === 0) {
        // If the quantity becomes zero, remove the pizza from the cart
        const updatedCart = cartItems.filter(
          (pizza) => pizza.name !== pizzaName
        );
        setCartItems(updatedCart);
      } else {
        // If the quantity is more than zero, update the quantity
        const updatedCart = cartItems.map((pizza) =>
          pizza.name === pizzaName
            ? { ...pizza, quantity: updatedQuantity }
            : pizza
        );
        setCartItems(updatedCart);
      }
    }
  };

  // Function to handle adding pizzas to the cart
  const addToCart = (pizzaObj) => {
    // Find the index of the pizza in the cartItems array based on its name
    const index = cartItems.findIndex((item) => item.name === pizzaObj.name);

    // If the pizza is already in the cart, update its quantity
    if (index !== -1) {
      // Create a copy of the cartItems array
      const updatedCartItems = [...cartItems];
      // Increase the quantity of the existing pizza in the cart
      updatedCartItems[index].quantity++;
      // Update the cartItems state with the updated array
      setCartItems(updatedCartItems);
    } else {
      // If the pizza is not in the cart, add it with a quantity of 1
      // Create a new object by spreading the pizzaObj and adding a quantity property
      setCartItems([...cartItems, { ...pizzaObj, quantity: 1 }]);
    }
  };

  // Render the components: Header, Menu, and Footer
  return (
    <>
      {/* Header component with cartItems and handleDelete function as props */}
      <Header cartItems={cartItems} handleDelete={handleDelete} />
      {/* Main content container */}
      <div className="container">
        {/* Menu component with addToCart and handleDelete functions as props */}
        <Menu addToCart={addToCart} handleDelete={handleDelete} />
        {/* Footer component */}
        <Footer />
      </div>
    </>
  );

  return (
    <>
      <Header cartItems={cartItems} handleDelete={handleDelete} />
      <div className="container">
        <Menu addToCart={addToCart} handleDelete={handleDelete} />
        <Footer />
      </div>
    </>
  );
}

// @ts-ignore
const root = ReactDOM.createRoot(document.getElementById("root"));
root.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>
);
