import React, { useState } from "react";
import ReactDOM from "react-dom/client";
import "./index.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faCartShopping } from "@fortawesome/free-solid-svg-icons";

const pizzaData = [
  {
    name: "Focaccia",
    ingredients: "Bread with italian olive oil and rosemary",
    price: 6,
    photoName: "pizzas/focaccia.jpg",
    soldOut: false,
  },
  {
    name: "Pizza Margherita",
    ingredients: "Tomato and mozarella",
    price: 10,
    photoName: "pizzas/margherita.jpg",
    soldOut: true,
  },
  {
    name: "Pizza Spinaci",
    ingredients: "Tomato, mozarella, spinach, and ricotta cheese",
    price: 12,
    photoName: "pizzas/spinaci.jpg",
    soldOut: false,
  },
  {
    name: "Pizza Funghi",
    ingredients: "Tomato, mozarella, mushrooms, and onion",
    price: 12,
    photoName: "pizzas/funghi.jpg",
    soldOut: false,
  },
  {
    name: "Pizza Salamino",
    ingredients: "Tomato, mozarella, and pepperoni",
    price: 15,
    photoName: "pizzas/salamino.jpg",
    soldOut: true,
  },
  {
    name: "Pizza Prosciutto",
    ingredients: "Tomato, mozarella, ham, aragula, and burrata cheese",
    price: 18,
    photoName: "pizzas/prosciutto.jpg",
    soldOut: false,
  },
];

function App() {
  const [cartItems, setCartItems] = useState([""]);

  const addToCart = (pizzaObj) => {
    const existingItemIndex = cartItems.findIndex(
      (item) => item.name === pizzaObj.name
    );

    if (existingItemIndex !== -1) {
      // If the item exists, update its quantity
      const updatedCartItems = [...cartItems];
      updatedCartItems[existingItemIndex].quantity += 1;
      setCartItems(updatedCartItems);
    } else {
      // If the item doesn't exist, add it with a quantity of 1
      setCartItems((prevCartItems) => [
        ...prevCartItems,
        { ...pizzaObj, quantity: 1 },
      ]);
    }
  };

  return (
    <>
      <Header cartItems={cartItems} />
      <div className="container">
        <Menu addToCart={addToCart} />
        <Cart items={cartItems} />
        <Footer />
      </div>
    </>
  );
}

function Header({ cartItems }) {
  const [cartVisible, setCartVisible] = useState(false);

  const toggleCart = () => {
    setCartVisible(!cartVisible);
  };

  return (
    <header className="header">
      <span className="cart">
        <h1>Ã–zcan's React Pizzeria</h1>
        <FontAwesomeIcon
          icon={faCartShopping}
          size="4x"
          style={{ color: "#ff5e1a" }}
          onClick={toggleCart}
        />
      </span>
      {cartVisible && <Cart items={cartItems} />}
    </header>
  );
}

function Cart({ items }) {
  const total = items.reduce((acc, pizzaObj) => {
    const price = typeof pizzaObj.price === "number" ? pizzaObj.price : 0;
    return acc + price;
  }, 0);

  return (
    <div className="cart-container">
      <h2>Shopping Cart</h2>
      <ul>
        {items.map((pizzaObj) => (
          <li key={pizzaObj.name}>
            {pizzaObj.name} - {pizzaObj.price}â‚¬
          </li>
        ))}
      </ul>
      <p>Total: {total}â‚¬</p>
    </div>
  );
}

function Menu({ addToCart }) {
  const pizzas = pizzaData;
  const numPizzas = pizzas.length;

  return (
    <main className="menu">
      <h2>Our menu</h2>
      {numPizzas > 0 ? (
        <>
          <p>
            Authentic Italian cuisine. 6 creative dishes to choose from. All
            from our stone oven, all organic, all delicious.
          </p>

          <ul className="pizzas">
            {pizzas.map((pizza) => (
              <Pizza pizzaObj={pizza} key={pizza.name} addToCart={addToCart} />
            ))}
          </ul>
        </>
      ) : (
        <p>We're still working on our menu. Please come back later :)</p>
      )}
    </main>
  );
}

function Pizza({ pizzaObj, addToCart }) {
  const [num, setNum] = useState(0);

  const handleClick = () => {
    addToCart(pizzaObj);
    setNum(num + 1);
  };

  return (
    <li className={`pizza ${pizzaObj.soldOut ? "sold-out" : ""}`}>
      <img src={pizzaObj.photoName} alt={pizzaObj.name} />
      <div>
        <h3>{pizzaObj.name}</h3>
        <p>{num}</p>
        <p>{pizzaObj.ingredients}</p>
        <span className="add">
          {pizzaObj.soldOut ? "SOLD OUT" : pizzaObj.price + "â‚¬"}
          {pizzaObj.soldOut === false ? (
            <button className="cartBtn" onClick={handleClick}>
              Add to Cart
            </button>
          ) : null}
        </span>
      </div>
    </li>
  );
}

function Footer() {
  const hour = new Date().getHours();
  const openHour = 12;
  const closeHour = 22;
  const isOpen = hour >= openHour && hour <= closeHour;
  console.log(isOpen);

  return (
    <footer className="footer">
      {isOpen ? (
        <Order closeHour={closeHour} openHour={openHour} />
      ) : (
        <p>
          We're happy to welcome you between {openHour}:00 and {closeHour}:00.
        </p>
      )}
    </footer>
  );
}

function Order({ closeHour, openHour }) {
  return (
    <div className="order">
      <p>
        We're open from {openHour}:00 to {closeHour}:00. Come visit us or order
        online.
      </p>
      <button className="btn">Order</button>
    </div>
  );
}

// @ts-ignore
const root = ReactDOM.createRoot(document.getElementById("root"));
root.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>
);
