import React from "react";

export function Cart({ items, onDelete }) {
  const groupedItems = items.reduce((acc, pizzaObj) => {
    const existingItem = acc.find((item) => item.name === pizzaObj.name);
    if (existingItem) {
      existingItem.quantity += pizzaObj.quantity;
    } else {
      acc.push({ ...pizzaObj });
    }
    return acc;
  }, []);

  const total = groupedItems.reduce((acc, pizzaObj) => {
    const price = typeof pizzaObj.price === "number" ? pizzaObj.price : 0;
    return acc + price * pizzaObj.quantity;
  }, 0);

  return (
    <div className="cart-container">
      <h2>Shopping Cart</h2>
      <ul>
        {groupedItems.map((pizzaObj) => (
          <li className="pizzaList" key={pizzaObj.name}>
            {pizzaObj.name} - {pizzaObj.price}€ x {pizzaObj.quantity} ={" "}
            {pizzaObj.price * pizzaObj.quantity}€
            <button
              className="deleteButton"
              onClick={() => onDelete(pizzaObj.name)}
            >
              Delete
            </button>
          </li>
        ))}
      </ul>
      <p>Total: {total}€</p>
    </div>
  );
}
