import React, { useState } from "react";
import ReactDOM from "react-dom/client";
import "./index.css";
import { Header } from "./Header";
import { Menu } from "./Menu";
import { Footer } from "./Footer";

export const pizzaData = [
  // your pizza data here
];

function App() {
  const [cartItems, setCartItems] = useState([]); // Initialize with an empty array

  const handleDelete = (itemName) => {
    const updatedCart = cartItems.filter((item) => item.name !== itemName);
    setCartItems(updatedCart);
  };

  const addToCart = (pizzaObj) => {
    const index = cartItems.findIndex((item) => item.name === pizzaObj.name);
    if (index !== -1) {
      const updatedCartItems = [...cartItems];
      updatedCartItems[index].quantity++;
      setCartItems(updatedCartItems);
    } else {
      setCartItems([...cartItems, { ...pizzaObj, quantity: 1 }]);
    }
  };

  return (
    <>
      <Header cartItems={cartItems} handleDelete={handleDelete} />
      <div className="container">
        <Menu addToCart={addToCart} />
        <Footer />
      </div>
    </>
  );
}

const root = ReactDOM.createRoot(document.getElementById("root"));
root.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>
);
